    private void jcbTarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jcbTarActionPerformed
        if(jcbTar.isSelected()){
            jcbMan.setEnabled(false);
            jcbNoi.setEnabled(false);
        }else{
            jcbMan.setEnabled(true);
            jcbNoi.setEnabled(true);
        }
    }//GEN-LAST:event_jcbTarActionPerformed

