    private void btnMatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMatActionPerformed
        String nome = txtNome.getText();
        String unidade = jcbUni.getSelectedItem().toString();
        String curso = null, turno = null;
        
        if(nome.isBlank()){
            nome = "Não informado";
        }
        
        if(rdoTec.isSelected()){
            curso = "Técnico";
        }else if(rdoTecno.isSelected()){
            curso = "Tecnólogo";
        }else{
            curso = "Não selecionado";
        }
        
        if(jcbMan.isSelected()){
            turno = "Manhã";
        }else if(jcbTar.isSelected()){
            turno = "Tarde";
        }else if(jcbNoi.isSelected()){
            turno = "Noite";
        }else{
            turno = "Não selecionado";
        }
        
        if(unidade.equals("<Selecione>")){
            unidade = "Não selecionado";
        }
        
        lblResult.setVisible(true);
        lblNom.setVisible(true);
        lblCur.setVisible(true);
        lblTur.setVisible(true);
        lblUni.setVisible(true);
        
        lblNom.setText("Nome:   "+nome);
        lblCur.setText("Curso:   "+curso);
        lblTur.setText("Turno:   "+turno);
        lblUni.setText("Unidade:   "+unidade);
    }//GEN-LAST:event_btnMatActionPerformed

